import React from "react";
import { useParams, Route } from "react-router-dom";
import Comments from "../components/comments/Comments";

const QuoteDetails = () => {
   const param = useParams();

   console.log(param);
   return (
      <>
         <h1>Quote Details</h1>
         <p>{param.quote}</p>
         <Route path={`${param.quote}/comments`}>
            <Comments />
         </Route>
      </>
   );
};

export default QuoteDetails;
