import { Fragment } from "react";
import { useHistory, useLocation } from "react-router-dom";
import QuoteItem from "./QuoteItem";
import classes from "./QuoteList.module.css";

const sortQuotes = (quotes, ascending) => {
   return quotes.sort((quoteA, quoteB) => {
      if (ascending) {
         return quoteA.id > quoteB.id ? 1 : -1;
      } else {
         return quoteA.id < quoteB.id ? 1 : -1;
      }
   });
};

const QuoteList = (props) => {
   const history = useHistory();
   const location = useLocation();
   console.log(location.search);

   const changeSortingHandler = () => {
      history.push(`/quotes?sort=${isSortingAscending ? "desc" : "asc"}`);
   };

   // this will give me a better constructure object value instead of ?sort=asc
   const queryParams = new URLSearchParams(location.search);

   // we use get to get the key which from it ?sort=asc so it is sort
   const SortingAscending = queryParams.get("sort");

   // checking
   const isSortingAscending = SortingAscending === "asc";

   return (
      <Fragment>
         <div className={classes.sorting}>
            <button onClick={changeSortingHandler}>
               Sort {isSortingAscending ? "Descending" : "Ascending"}
            </button>
         </div>
         <ul className={classes.list}>
            {props.quotes.map((quote) => (
               <QuoteItem
                  key={quote.id}
                  id={quote.id}
                  author={quote.author}
                  text={quote.text}
               />
            ))}
         </ul>
      </Fragment>
   );
};

export default QuoteList;
